import React from "react";
import './style.css';


const Recipes = ({ subtitle, image }) => {
  const recipes = document.querySelector('hidden');
  function toggleOpen() {
    this.classList.toggle('hidden');
  }

  recipes.forEach(recipes => recipes.addEventListener('mouseover', toggleOpen));


  return (
    <>
      <div className="recipes">
        <h3 className="hidden">{ subtitle }</h3>
        <img src={ image } alt="" />
      </div>
    </>
  )
}

export default Recipes;
