import React from 'react';
import { Card } from 'react-bootstrap';
import './style.css';

// import animalsImg from '../images/animals.png';
// import environmentImg from '../images/environment.png';
// import healthImg from '../images/health.png';

// import Card from 'react-bootstrap/Card';

const Reason = ({ image, cardTitle, text }) => {
  return (
    <div className='reason'>
      <Card className='card'>
        <img src={ image } alt='' className='img' />
        <Card.Body>
          <Card.Title className='card-title'>{ cardTitle }</Card.Title>
          <Card.Text>
            { text }  
          </Card.Text>
        </Card.Body>
      </Card>
    </div>
  )
}

export default Reason;
